import classNames from 'classnames'
import PropTypes from 'prop-types'

import LoupeIcon from '../icons/LoupeIcon'
import { getProducts } from '../../slices/catalogProductsSlice'

import './style.scss'
import { useNavigate } from 'react-router-dom'
import { useState } from 'react'
import { useDispatch } from 'react-redux'

const Search = ({ className }) => {
  const [state, setState] = useState(null)
  const dispatch = useDispatch()
  const navigate = useNavigate()
  const SearchChange = (e) => {
    setState(e.target.value)
  }

  const Search = async (e) => {
    e.preventDefault()
    try {
      await dispatch(getProducts(e.target.value, 9)).unwrap()
      navigate(`/catalog?&page_size=9&str=${state}`)
    } catch (error) {
      console.log(error)
    }
  }
  return (
    <div className={classNames('search', className)}>
      <input
        className='search__input'
        type='text'
        placeholder='Поиск в каталоге'
        onChange={SearchChange}
      />
      <button className='search__button' onClick={Search}>
        <LoupeIcon className='search__loupe' />
      </button>
    </div>
  )
}

Search.propTypes = {
  className: PropTypes.string,
}

export default Search
