import PropTypes from 'prop-types'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom'

//import { login } from '../../../slices/authSlice'
import { EmailField, PasswordField } from '../../form-fields'
import LoadingButton from '../../LoadingButton'
import BaseForm from '../BaseForm'
import AuthService from '../../../services/authService'
import { usePromiseTracker, trackPromise } from "react-promise-tracker"

const LoginForm = ({ user, setUser, setServerError }) => {
  const { promiseInProgress } = usePromiseTracker();
  const navigate = useNavigate()

  const onSubmit = async (user, setError) => {
    try {
      trackPromise(AuthService.login())
      navigate('/')
    } catch (error) {
      if (error.field) {
        setError(error.field, { type: 'custom', message: error.detail })
      } else {
        setServerError('Непредвиденная ошибка')
      }
    }
  }
  const emailRegExp =
  /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/

  const patternObject = {
    value: emailRegExp,
    message: 'Введите корректную почту',
  }
  let rules = {
    pattern: patternObject,
  }
  return (
    <form className='form' onSubmit={submitHandler}>
    <BaseField type='text' rules={rules} name='email' placeholder='Электронная почта' required />
    </form>


