import axios from 'axios'
import { HOST_URL, publicApi } from '../api'

class ProductService {
  static async getPopularBooks() {
    const instance = axios.create({baseURL: "http://localhost:83"});
    const res = await instance.get("/api/popular") //publicApi.get('/popular')
    return res.data
  }

  static async getDiscountedBooks() {
    const res = await publicApi.get("/api/discount")
    return res.data
  }

}

export default ProductService
