import { withCredentialsApi, authApi, publicApi } from '../api'

class AuthService {
  static async login(user) {
    const res = await publicApi.post('/login', user)
    return res.data
  }

  static async register(user) {
    const res = await publicApi.post('/register', user)
    return res.data
  }

  static async verify(user) {
    const res = await publicApi.post('/verify', user)
    return res.data
  }

  static async logout() {
    await authApi.post('/logout')
  }

  /*static async checkAuth() {
    const res = await withCredentialsApi.post('/login/refresh')
    return res.data
  }*/
}

export default AuthService
