import { HOST_URL, publicApi } from '../api'

class ProductService {
  static async getPopularBooks() {
    const res = await publicApi.post('/popular')
    return res.data
  }

  static async getDiscountedBooks() {
    const res = await publicApi.post('/discount')
    return res.data
  }

  static async getDiscountedBooks() {
    const res = await publicApi.post('/discount')
    return res.data
  }

  static async addFav(id) {
    const res = await publicApi.post('/addFavourite', id)
    return res.data
  }

  static async removeFav(id) {
    const res = await publicApi.post('/deleteFavourite', id)
    return res.data
  }

  static async getFav(id) {
    const res = await publicApi.post('/getFavourites', id)
    return res.data
  }

  static async getBook(id) {
    const res = await publicApi.post('/book', id)
    return res.data
  }
}

export default ProductService
